CREATE OR REPLACE FUNCTION obter_status_contrato ( nr_sequencia_p bigint, ie_tipo_status_p text) RETURNS varchar AS $body$
DECLARE


dt_fim_w		timestamp;
qt_dias_rescisao_w	integer;
qt_dias_aviso_venc_w	integer;

/* ie_tipo_status_p
V - Vencido
R - Prazo Renovacao
*/
BEGIN

select	trunc(dt_fim),
	qt_dias_aviso_venc,
	qt_dias_rescisao
into STRICT	dt_fim_w,
	qt_dias_aviso_venc_w,
	qt_dias_rescisao_w
from	contrato
where	nr_sequencia = nr_sequencia_p;

if (ie_tipo_status_p = 'V') then /* VENCIDOS */
	if (trunc(clock_timestamp()) > trunc(dt_fim_w)) and (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') then
		return 'S';
	else
		return 'N';
	end if;
end if;

if (ie_tipo_status_p = 'R') then /* Prazo Renovacao */
	if (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') and (qt_dias_aviso_venc_w IS NOT NULL AND qt_dias_aviso_venc_w::text <> '') and (trunc(clock_timestamp()) <= trunc(dt_fim_w)) and
		(trunc(clock_timestamp()) >= (trunc(dt_fim_w) - coalesce(qt_dias_aviso_venc_w,0))) then
		return 'S';
	else
		return 'N';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
