SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_cot_forn_internet ( nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w			bigint;
qt_fornecedores_w			bigint;
qt_confirmados_w			bigint;
ie_status_w			varchar(15);
dt_geracao_ordem_compra_w	timestamp;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	cot_compra_forn
where	nr_cot_compra = nr_cot_compra_p
and	coalesce(ie_liberada_internet,'N') = 'S'
and	coalesce(ie_status,'X') <> 'CF'; --não conta as canceladas
if (qt_registro_w > 0) then
	ie_status_w	:= 'E'; -- Enviada
	select	count(*)
	into STRICT	qt_fornecedores_w
	from	cot_compra_forn
	where	nr_cot_compra = nr_cot_compra_p
	and	coalesce(ie_status,'X') <> 'CF'; --não conta as canceladas
	select	count(*)
	into STRICT	qt_confirmados_w
	from	cot_compra_forn
	where	nr_cot_compra = nr_cot_compra_p
	and	ie_status = 'FF' --Finalizadas pelo fornecedor
	and	coalesce(ie_status,'X') <> 'CF'; --não conta as canceladas
	if (qt_confirmados_w > 0) and (qt_confirmados_w < qt_fornecedores_w) then
		ie_status_w	:= 'PC'; -- Possui alguma confirmação
	elsif (qt_confirmados_w > 0) and (qt_confirmados_w >= qt_fornecedores_w) then
		ie_status_w	:= 'TC'; -- Todos fornecedores confirmaram
	end if;
end if;

select	dt_geracao_ordem_compra
into STRICT	dt_geracao_ordem_compra_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (dt_geracao_ordem_compra_w IS NOT NULL AND dt_geracao_ordem_compra_w::text <> '') then
	ie_status_w := 'F'; -- Finalizada. Já possui ordem de compra
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_cot_forn_internet ( nr_cot_compra_p bigint) FROM PUBLIC;

