CREATE OR REPLACE FUNCTION obter_status_disp_consolidacao (cd_material_p bigint, qt_dispensacao_p bigint, qt_dose_consumo_p bigint, QT_DOSE_INT_p bigint, QT_DISP_INT_P bigint, DS_JUSTIFICATIVA_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ie_tolera_w		varchar(30);


BEGIN

ie_tolera_w	:= obter_se_tolera_variacao_disp(cd_material_p,qt_dispensacao_p,qt_dose_consumo_p);

if (ie_tolera_w = 'N') then
	if (qt_dose_int_p <> QT_DISP_INT_P) and (coalesce(ds_justificativa_p::text, '') = '') then
		if (ie_opcao_p = 'C') then
			ds_retorno_w	:= 'AV';
		else
			/* % alta de variação entre dispensado e utilizado */

			ds_retorno_w	:= substr(obter_desc_expressao(330476,null),1,255);
		end if;
	elsif (qt_dose_int_p <> QT_DISP_INT_p) and (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
		if (ie_opcao_p = 'C') then
			ds_retorno_w	:= 'DJ';
		else
			/* Dispensação justificada */

			ds_retorno_w	:= substr(obter_desc_expressao(330806,null),1,255);
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
