CREATE OR REPLACE FUNCTION obter_status_disp_suep ( nr_seq_pac_disp_p bigint, ie_opcap_p text default 'D') RETURNS varchar AS $body$
DECLARE


/*

*/
dt_retirada_prev_w	timestamp;
dt_retirada_w	timestamp;
ds_retorno_w	varchar(255);

ds_status_w		varchar(255);
ie_status_w		varchar(255);



BEGIN

if (nr_seq_pac_disp_p IS NOT NULL AND nr_seq_pac_disp_p::text <> '') then

	select	a.dt_retirada_prev,
			a.dt_retirada
	into STRICT	dt_retirada_prev_w,
			dt_retirada_w
	from	atend_pac_dispositivo a
	where	a.nr_sequencia = nr_seq_pac_disp_p;

	if ((coalesce(dt_retirada_w::text, '') = '') and (clock_timestamp() >= dt_retirada_prev_w)) then
	    ds_status_w :=  substr(obter_desc_expressao(330951),1,100); -- Expirado.
		ie_status_w := 'E';
	elsif ((coalesce(dt_retirada_w::text, '') = '') and (clock_timestamp() between dt_retirada_prev_w -1 and dt_retirada_prev_w)) then
	    ds_status_w :=  substr(obter_desc_expressao(330479),1,100); -- A Expirar.
		ie_status_w := 'A';
	else
	    ds_status_w :=  substr(obter_desc_expressao(294264),1,100); -- Normal.
		ie_status_w := 'N';
	end if;
end if;

if (ie_opcap_p = 'D') then
	ds_retorno_w := ds_status_w;
else
	ds_retorno_w := ie_status_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
