CREATE OR REPLACE FUNCTION obter_status_entrega_oci ( cd_fornecedor_p text, dt_prevista_entrega_p timestamp ) RETURNS varchar AS $body$
DECLARE


qt_entrega_confirmada_w       bigint;
qt_entrega_nao_confirmada_w   bigint;
ie_status_w                   varchar(1) := '';


BEGIN

select sum(CASE WHEN b.ie_status='S' THEN  1  ELSE 0 END ),
       sum(CASE WHEN b.ie_status='N' THEN  1  ELSE 0 END )
into STRICT   qt_entrega_confirmada_w,
       qt_entrega_nao_confirmada_w
from   ordem_compra a,
       ordem_compra_item_entrega b
where  a.nr_ordem_compra = b.nr_ordem_compra
and    b.dt_prevista_entrega = trunc(dt_prevista_entrega_p)
and    coalesce(a.cd_cgc_fornecedor,a.cd_pessoa_fisica) = cd_fornecedor_p
and    ie_status in ('S','N');

if (qt_entrega_confirmada_w > 0) then
  ie_status_w := 'S';
elsif (qt_entrega_nao_confirmada_w > 0) then	
  ie_status_w := 'N';	
end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
