CREATE OR REPLACE FUNCTION obter_status_equip_agenda ( nr_seq_agenda_p bigint, cd_equipamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_w		varchar(255):='';
qt_equipamento_w	bigint;


BEGIN

if (cd_equipamento_p IS NOT NULL AND cd_equipamento_p::text <> '') then
	select	count(*)
	into STRICT	qt_equipamento_w
	from	consistencia_agenda_cir
	where	nr_seq_agenda 	= nr_seq_agenda_p
	and	cd_equipamento	= cd_equipamento_p;

	if (qt_equipamento_w > 0) then
		ds_status_w := wheb_mensagem_pck.get_texto(309381); -- Indisponível
	else
		ds_status_w := wheb_mensagem_pck.get_texto(309380);-- Disponível
	end if;
end if;


return	ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
