CREATE OR REPLACE FUNCTION obter_status_estrutura_livros (nr_seq_estrutura_p text) RETURNS varchar AS $body$
DECLARE

ie_situacao_w		varchar(1):='I';

/*
  Busca o status da estrutura através do NR_SEQ_ESTRUTURA na Tabela  FIS_LIVROS_ESTR_PADRAO
  Possíveis retorno;
  I =  Inativo
  A = Ativo
  'N/D' = Nenhum resultado encontrado
*/
			

BEGIN
     begin
       select coalesce(ie_situacao, 'I')
       into STRICT ie_situacao_w
       from fis_livros_estr_padrao
       where  nr_sequencia  = nr_seq_estrutura_p;
      end;

return	ie_situacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
