CREATE OR REPLACE FUNCTION obter_status_est_hemocomp_jap (tipo_retorno_p text, nr_sequencia_p bigint, tipo_estoque_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50);

/*tipo_retorno_p
  C = Codigo da epressao
  E = String da Expressao*/
BEGIN

if (tipo_estoque_p = 'Reservado') then
	if (tipo_retorno_p = 'E') then
		
	    SELECT  CASE  WHEN  (spv.nr_sequencia IS NOT NULL AND spv.nr_sequencia::text <> '') AND IE_RESERVADO = 'S' AND coalesce(spv.NR_SEQ_TRANSFUSAO::text, '') = '' THEN OBTER_DESC_EXPRESSAO(331547)
            WHEN (spv.nr_sequencia IS NOT NULL AND spv.nr_sequencia::text <> '') 	AND	(spv.dt_inicio_infusao IS NOT NULL AND spv.dt_inicio_infusao::text <> '') THEN OBTER_DESC_EXPRESSAO(489101) 
            WHEN (spv.nr_sequencia IS NOT NULL AND spv.nr_sequencia::text <> '')  AND coalesce(spv.dt_inicio_infusao::text, '') = '' THEN OBTER_DESC_EXPRESSAO(311227) ELSE OBTER_DESC_EXPRESSAO(331547) END as status
        INTO STRICT	ds_retorno_w
		FROM	san_producao_v spv
		WHERE spv.nr_sequencia = nr_sequencia_p;

	else
		SELECT  CASE  WHEN (spv.nr_sequencia IS NOT NULL AND spv.nr_sequencia::text <> '') AND ie_reservado = 'S' AND coalesce(spv.nr_seq_transfusao::text, '') = '' THEN '331547'
		WHEN (spv.nr_sequencia IS NOT NULL AND spv.nr_sequencia::text <> '') AND	(spv.dt_inicio_infusao IS NOT NULL AND spv.dt_inicio_infusao::text <> '') THEN '489101'
		WHEN (spv.nr_sequencia IS NOT NULL AND spv.nr_sequencia::text <> '') AND	coalesce(spv.dt_inicio_infusao::text, '') = '' THEN '311227' ELSE '331547' END as status
		INTO STRICT	ds_retorno_w
		FROM	san_producao_v spv
		WHERE spv.nr_sequencia = nr_sequencia_p;
	end if;
else
	if (tipo_retorno_p = 'E') then
		ds_retorno_w := OBTER_DESC_EXPRESSAO(1035655);
	else
		ds_retorno_w := '1035655';
	end if;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
