CREATE OR REPLACE FUNCTION obter_status_etapa_sol ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint) RETURNS varchar AS $body$
DECLARE

ie_alteracao_w		bigint;
ds_retorno_w		varchar(10);
nr_seq_motivo_w		bigint;
ie_troca_frasco_w	varchar(5);

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') then

	select	max(ie_alteracao),
		coalesce(max(nr_seq_motivo),0)
	into STRICT	ie_alteracao_w,
		nr_seq_motivo_w
	from	prescr_solucao_evento
	where	nr_prescricao = nr_prescricao_p
	and	ie_tipo_solucao = 1
	and	ie_evento_valido = 'S'
	and	ie_alteracao in (1,2,3,4,12,35)
	and	nr_seq_solucao	= nr_seq_solucao_p
	and 	nr_etapa_evento = nr_etapa_p
	and 	dt_alteracao = (SELECT	max(dt_alteracao)
				from	prescr_solucao_evento
				where	nr_prescricao = nr_prescricao_p
				and	ie_tipo_solucao = 1
				and	ie_evento_valido = 'S'
				and	ie_alteracao in (1,2,3,4,12,35)
				and 	nr_etapa_evento = nr_etapa_p
				and	nr_seq_solucao	= nr_seq_solucao_p);

	if (ie_alteracao_w = 1) or (ie_alteracao_w = 3) or (ie_alteracao_w = 35)then
		ds_retorno_w := 'I';
	elsif (ie_alteracao_w = 2) then
		if (nr_seq_motivo_w > 0) then
			select	coalesce(ie_troca_frasco,'N')
			into STRICT	ie_troca_frasco_w
			from	adep_motivo_interrupcao
			where	nr_sequencia = nr_seq_motivo_w;
			if (ie_troca_frasco_w = 'S') then
				ds_retorno_w := 'T';
			else
				ds_retorno_w := 'INT';
			end if;
		else
			ds_retorno_w := 'INT';
		end if;
	elsif (ie_alteracao_w = '4') then
		ds_retorno_w := 'T';
	elsif (ie_alteracao_w = '12') then
		ds_retorno_w := 'S';
	end if;
return ds_retorno_w;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
