CREATE OR REPLACE FUNCTION obter_status_evento_agenda (cd_estabelecimento_p bigint, ie_evento_p text, ie_agenda_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_evento_sap_w	varchar(1);
ie_status_w		varchar(3);
cd_perfil_w		bigint;


BEGIN

select	substr(coalesce(obter_se_existe_evento_agenda(cd_estabelecimento_p,ie_evento_p,ie_agenda_p),'N'),1,1)
into STRICT	ie_existe_evento_sap_w
;

cd_perfil_w := obter_perfil_ativo;

if (coalesce(cd_estabelecimento_p,0) > 0) and (ie_evento_p IS NOT NULL AND ie_evento_p::text <> '') and (ie_agenda_p IS NOT NULL AND ie_agenda_p::text <> '') and (ie_existe_evento_sap_w = 'S') then
	select	max(a.ie_status)
	into STRICT	ie_status_w
	from	status_evento_agenda a
	where (a.cd_estabelecimento = cd_estabelecimento_p or exists (SELECT 1
									from STATUS_EVENTO_AGENDA_ESTAB x 
									where x.cd_estabelecimento = cd_estabelecimento_p 
									and a.nr_sequencia 	   = x.nr_seq_status_evento_ag ))
	and	a.ie_evento = ie_evento_p
	and	coalesce(a.cd_perfil,cd_perfil_w) = cd_perfil_w
	and	((a.ie_agenda = ie_agenda_p) or (a.ie_agenda = 'T'));
end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
