CREATE OR REPLACE FUNCTION obter_status_exec_ag_exame (nr_seq_agenda_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select 	max(obter_valor_dominio(1226,b.ie_status_execucao))
	into STRICT	ds_retorno_w
	from	prescr_procedimento b,
	prescr_medica a
	where	a.nr_prescricao	= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	b.cd_procedimento	=
				(SELECT cd_procedimento
				from agenda_paciente
				where	nr_sequencia	= nr_seq_agenda_p);
end if;

return	ds_retorno_w;

end;
