CREATE OR REPLACE FUNCTION obter_status_exec_laudo ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(40);


BEGIN

if (ie_opcao_p = 'D') then

select	substr(obter_valor_dominio(1226,max(b.ie_status_execucao)),1,40)
into STRICT	ds_retorno_w
from	procedimento_paciente c,
	prescr_procedimento b
where	c.nr_prescricao			= b.nr_prescricao
and	c.nr_sequencia_prescricao	= b.nr_sequencia
and	c.nr_laudo			= nr_sequencia_p;

elsif (ie_opcao_p = 'C') then

select	max(b.ie_status_execucao)
into STRICT	ds_retorno_w
from	procedimento_paciente c,
	prescr_procedimento b
where	c.nr_prescricao			= b.nr_prescricao
and	c.nr_sequencia_prescricao	= b.nr_sequencia
and	c.nr_laudo			= nr_sequencia_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
