CREATE OR REPLACE FUNCTION obter_status_farm_amb (nr_seq_paciente_p bigint, nr_ciclo_p bigint, dt_prevista_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select 	max(substr(obter_valor_dominio(3461,b.ie_status_medicacao),1,255))
into STRICT 	ds_retorno_w
from	fa_receita_farmacia a,
	fa_entrega_medicacao b
where	b.nr_seq_receita_amb = a.nr_sequencia
and	a.nr_seq_paciente = nr_seq_paciente_p
and	a.nr_ciclo	= nr_ciclo_p
and	dt_prevista_p between inicio_dia(b.dt_periodo_inicial) and fim_dia(b.dt_periodo_final);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
