CREATE OR REPLACE FUNCTION obter_status_funcao_html (cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE

/*	P = To do
	Y = Analysis
	E = Development
	R = Revision
	V = Verification
	L = Validation
	A = Accepted (RFLD)
	F = RFVD
	D = Done   */
ds_status_w		varchar(255);


BEGIN

select	CASE WHEN ie_status='P' THEN 'To Do' WHEN ie_status='Y' THEN obter_desc_expressao(667691)/*'Analysis'*/ WHEN ie_status='E' THEN obter_desc_expressao(310388)/*'Development'*/ WHEN ie_status='R' THEN obter_desc_expressao(297891)/*'Revision'*/ WHEN ie_status='V' THEN 'Verification' WHEN ie_status='L' THEN obter_desc_expressao(301227)/*'Validation'*/ WHEN ie_status='A' THEN 'Accepted (RFLD)' WHEN ie_status='F' THEN 'RFVD' WHEN ie_status='D' THEN 'Done' END
into STRICT	ds_status_w
from	funcoes_html5
where	cd_funcao = cd_funcao_p;

return	ds_status_w;

end;
$body$
