CREATE OR REPLACE FUNCTION obter_status_func_html5_proj ( DS_FUNCAO_P text) RETURNS varchar AS $body$
DECLARE

  IE_STATUS_W   varchar(1) := '';
  PR_RETORNO_W  varchar(15) := '';

BEGIN
  BEGIN
    SELECT IE_STATUS
      INTO STRICT IE_STATUS_W
      FROM FUNCOES_HTML5 FH,
           FUNCAO F
     WHERE FH.CD_FUNCAO = F.CD_FUNCAO
       AND trim(both UPPER(OBTER_DESC_EXPRESSAO_IDIOMA(CD_EXP_FUNCAO, DS_FUNCAO, 5))) = trim(both UPPER(DS_FUNCAO_P))
       AND (FH.IE_STATUS IS NOT NULL AND FH.IE_STATUS::text <> '')  LIMIT 1;
  EXCEPTION
    WHEN no_data_found THEN
      IE_STATUS_W := '';
    WHEN OTHERS THEN
      IE_STATUS_W := '';
  END;

  PR_RETORNO_W := OBTER_NOME_STATUS_FUNC_HTML5(IE_STATUS_W);

  RETURN  PR_RETORNO_W;

END;
$body$
