CREATE OR REPLACE FUNCTION obter_status_gasoterapia ( nr_seq_gasoterapia_p prescr_gasoterapia.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
 C - Codigo
D - Descricao 
*/
ie_status_w			varchar(255);
dt_liberacao_w		timestamp;
qt_hor_pendente_w	bigint;


BEGIN

select	coalesce(max(a.ie_suspenso),'N'),
		max(b.dt_liberacao)
into STRICT	ie_status_w,
		dt_liberacao_w
from	prescr_gasoterapia a,
		prescr_medica b
where	a.nr_prescricao	= b.nr_prescricao
and		a.nr_sequencia	= nr_seq_gasoterapia_p;

if (ie_status_w = 'N') then
	select	max(ie_evento)
	into STRICT	ie_status_w
	from	prescr_gasoterapia_evento a
	where	a.nr_seq_gasoterapia	= nr_seq_gasoterapia_p
	and		coalesce(a.ie_evento_valido,'S') = 'S'
	and		a.ie_evento not in (SELECT x.cd_registro from table(lista_pck.obter_lista_char('V,AI,O,RV,IS,IM,RP,CR,SH,OM')) x)
	and		a.nr_sequencia	= (	select	max(b.nr_sequencia)
								from	prescr_gasoterapia_evento b
								where	b.nr_seq_gasoterapia	= nr_seq_gasoterapia_p
								and		b.ie_evento not in (select x.cd_registro from table(lista_pck.obter_lista_char('V,AI,O,RV,IS,IM,RP,CR,SH,OM')) x)
								and		coalesce(b.ie_evento_valido,'S') = 'S');

	if (coalesce(ie_status_w::text, '') = '') and (coalesce(dt_liberacao_w::text, '') = '') then
		ie_status_w := 'P';
	elsif (coalesce(ie_status_w::text, '') = '') then
		ie_status_w := 'N';
	elsif (ie_status_w = 'A') then
		ie_status_w := 'N';
	elsif (ie_status_w in ('SM','SS','SP')) then
		ie_status_w := 'S';
	elsif (ie_status_w = 'TE') then
		select	count(a.nr_sequencia)
		into STRICT	qt_hor_pendente_w
		from	prescr_gasoterapia b,
				prescr_gasoterapia_hor a
		where	a.nr_prescricao		= b.nr_prescricao
		and		a.nr_seq_gasoterapia	= b.nr_sequencia
		and		b.nr_sequencia		= nr_seq_gasoterapia_p
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		coalesce(a.dt_fim_horario::text, '') = '' LIMIT 1;

		if (qt_hor_pendente_w	= 0) then
			ie_status_w := 'T';
		end if;
	end if;
end if;

if (coalesce(ie_status_w,'0') <> '0') then
	if (ie_opcao_p	= 'D') then
		ie_status_w	:= substr(obter_valor_dominio(2702, ie_status_w),1,255);
	end if;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
