SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_gasot_prescr ( nr_prescricao_p bigint, nr_seq_gasoterapia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w		varchar(3);
qt_vol_infundido_w		double precision;
qt_vol_desprezado_w	double precision;
nr_sequencia_w		bigint;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_gasoterapia_p IS NOT NULL AND nr_seq_gasoterapia_p::text <> '') then
	/* obter status conforme tipo solucao */

	/* obter se solução suspensa */

	select	coalesce(ie_suspenso,'N')
	into STRICT	ie_status_w
	from	prescr_gasoterapia
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_gasoterapia_p;

	if (ie_status_w <> 'S') then
		ie_status_w := '';
		/* obter sequência evento */

		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_sequencia_w
		from	prescr_gasoterapia_evento
		where	nr_seq_gasoterapia	= nr_seq_gasoterapia_p
		and	ie_evento	in ('I','IN','R','T')
		and	ie_evento_valido	= 'S';

		/* obter status solução */

		if (nr_sequencia_w > 0) then
			select	ie_evento
			into STRICT	ie_status_w
			from	prescr_gasoterapia_evento
			where	nr_sequencia = nr_sequencia_w;


		else
			ie_status_w := 'N';
		end if;
	end if;


end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
