SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_gas_anterior (nr_seq_gasoterapia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* **************
 C - Código
 D - Descrição
****************/
ie_status_w		varchar(5);


BEGIN

if (ie_opcao_p = 'C') then
	select	max(ie_evento)
	into STRICT	ie_status_w
	from	prescr_gasoterapia_evento a
	where	a.nr_seq_gasoterapia	= nr_seq_gasoterapia_p
	and		coalesce(a.ie_evento, 'X')		<> 'V'
	and		coalesce(a.ie_evento_valido,'S') = 'S'
	and		a.nr_sequencia		= (	SELECT	max(b.nr_sequencia)
									from	prescr_gasoterapia_evento b
									where	b.nr_seq_gasoterapia	= nr_seq_gasoterapia_p
									and		coalesce(b.ie_evento, 'X')		<> 'V'
									and		coalesce(b.ie_evento_valido,'S') = 'S');
elsif (ie_opcao_p  = 'CC') then
	select	max(ie_evento)
	into STRICT	ie_status_w
	from	prescr_gasoterapia_evento a
	where	a.nr_seq_gasoterapia	= nr_seq_gasoterapia_p
	and		coalesce(a.ie_evento_valido,'S') = 'S'
	and		a.nr_sequencia		= (	SELECT	max(b.nr_sequencia)
									from	prescr_gasoterapia_evento b
									where	b.nr_seq_gasoterapia	= nr_seq_gasoterapia_p
									and		coalesce(b.ie_evento_valido,'S') = 'S');
end if;

if (coalesce(ie_status_w,'0') <> '0') then
	if (ie_opcao_p	= 'D') then
		ie_status_w	:= substr(obter_valor_dominio(2702, ie_status_w),1,255);
	end if;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
