SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_hd_etapa ( nr_prescricao_p prescr_mat_hor.nr_prescricao%type, nr_seq_solucao_p prescr_solucao.nr_seq_solucao%type, nr_etapa_p prescr_mat_hor.nr_etapa_sol%type) RETURNS varchar AS $body$
DECLARE


ie_status_etapa_w	hd_prescricao_evento.ie_evento%type;

cEventos CURSOR FOR
SELECT	a.ie_evento
from	hd_prescricao_evento a
where	a.nr_prescricao = nr_prescricao_p
and		a.nr_seq_solucao = nr_seq_solucao_p
and		coalesce(a.nr_etapa_evento, 0) = coalesce(nr_etapa_p, 0)
and		coalesce(a.ie_evento_valido, 'S') = 'S'
and		a.ie_evento not in ('R', 'TF')
order by	dt_atualizacao_nrec;
BEGIN

ie_status_etapa_w := 'N';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	
	for cEventos_w in cEventos loop
		begin
			ie_status_etapa_w := cEventos_w.ie_evento;
		end;
	end loop;
	
	if (ie_status_etapa_w = 'NC' or ie_status_etapa_w = 'RPC') then
		ie_status_etapa_w := 'N';
	end if;

  if (ie_status_etapa_w = 'PC') then
    ie_status_etapa_w := 'Q';
  end if;
	
end if;

return	ie_status_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
