CREATE OR REPLACE FUNCTION obter_status_higien_cadastrado ( nr_Status_Higienizacao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w			varchar(1);
ie_apenas_cadastr_w		varchar(1);

BEGIN
ie_apenas_cadastr_w := Obter_param_Usuario(75, 85, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_apenas_cadastr_w);
ds_retorno_w := 'S';

if (nr_Status_Higienizacao_p IS NOT NULL AND nr_Status_Higienizacao_p::text <> '') and (ie_apenas_cadastr_w = 'S') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from 	sl_status_higienizacao
	where 	ie_status_higienizacao = UPPER(nr_Status_Higienizacao_p);

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
