CREATE OR REPLACE FUNCTION obter_status_hor_proced ( dt_administracao_p timestamp, dt_suspensao_p timestamp, ie_dose_especial_p text, ie_status_recoleta_p text default null, ie_status_atend_p text default null, ie_util_status_recol_p text default null) RETURNS varchar AS $body$
DECLARE


ie_status_w	varchar(15);


BEGIN
if (ie_status_recoleta_p	= ie_status_atend_p) and (ie_util_status_recol_p = 'S') then
	ie_status_w	:= 'R';
elsif (coalesce(dt_administracao_p::text, '') = '') and (coalesce(dt_suspensao_p::text, '') = '') then
	begin
	if (ie_dose_especial_p = 'S') then
		begin
		ie_status_w	:= 'E';
		end;
	else
		begin
		ie_status_w	:= 'N';
		end;
	end if;
	end;
elsif (dt_administracao_p IS NOT NULL AND dt_administracao_p::text <> '') and (coalesce(dt_suspensao_p::text, '') = '') then
	begin
	ie_status_w	:= 'A';
	end;
elsif (coalesce(dt_administracao_p::text, '') = '') and (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then
	begin
	ie_status_w	:= 'S';
	end;
elsif (dt_administracao_p IS NOT NULL AND dt_administracao_p::text <> '' AND dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then
	if (dt_administracao_p < dt_suspensao_p) then
		ie_status_w	:= 'A';
	else
		ie_status_w	:= 'S';
	end if;
end if;

return ie_status_w;

end;
