SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_hor_sol_adep_esp ( dt_instalacao_p timestamp, dt_administracao_p timestamp, dt_suspensao_p timestamp, dt_interrupcao_p timestamp, ie_dose_especial_p text, nr_seq_processo_p bigint, nr_seq_area_prep_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_solucao_especial_p text, ie_param673_p text, nr_seq_mat_hor_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_status_processo_w	varchar(15)	:= 'X';
ie_status_w		varchar(15);
ie_status_sol_w		varchar(5)	:= '';
nr_seq_mat_cpoe_w	bigint;
dt_horario_w		timestamp;
qt_reg_w		bigint;


BEGIN
	
if	(ie_param673_p = 'S' AND ie_solucao_especial_p = 'S') then
	ie_status_sol_w := substr(obter_status_solucao_prescr(1,nr_prescricao_p,nr_seq_solucao_p),1,3);
end if;

if (coalesce(dt_instalacao_p::text, '') = '') and (coalesce(dt_administracao_p::text, '') = '') and (coalesce(dt_suspensao_p::text, '') = '') then
	begin
		
	if (nr_seq_mat_hor_p IS NOT NULL AND nr_seq_mat_hor_p::text <> '') then
	
		select	max(b.nr_seq_mat_cpoe),
			max(a.dt_horario)
		into STRICT	nr_seq_mat_cpoe_w,
			dt_horario_w
		from	prescr_mat_hor a,
			prescr_material b
		where	a.nr_prescricao 	= b.nr_prescricao
		and	a.nr_seq_material 	= b.nr_sequencia
		and	a.nr_sequencia 		= nr_seq_mat_hor_p;
		
		select	count(1)
		into STRICT	qt_reg_w
		from	cpoe_susp_medication
		where	nr_seq_mat_cpoe = nr_seq_mat_cpoe_w
		and	dt_horario_w between dt_start and dt_end
		and	coalesce(dt_inactive::text, '') = '';
		
		if (qt_reg_w > 0) then
			return 'S'; --Suspenso
		end if;
		
	end if;
		
	
	if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then
		begin
		--ie_status_processo_w	:= obter_status_processo_area(nr_seq_processo_p,nr_seq_area_prep_p);
		ie_status_processo_w	:= obter_status_processo(nr_seq_processo_p);
		end;
	end if;
	if (ie_status_processo_w in ('D','H','P','L','R','A')) then
		ie_status_w	:= ie_status_processo_w;
	elsif (ie_dose_especial_p = 'S') then
		ie_status_w	:= 'E';
	else
		ie_status_w	:= 'N';
	end if;	
	end;
elsif (coalesce(dt_administracao_p::text, '') = '') and (coalesce(dt_suspensao_p::text, '') = '') and (coalesce(dt_interrupcao_p::text, '') = '') and (dt_instalacao_p IS NOT NULL AND dt_instalacao_p::text <> '') then
	begin
	ie_status_w	:= 'I';
	end;
elsif (dt_administracao_p IS NOT NULL AND dt_administracao_p::text <> '') and (coalesce(dt_suspensao_p::text, '') = '') then
	begin
	ie_status_w	:= 'A';
	end;
elsif (coalesce(dt_administracao_p::text, '') = '') and (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then
	begin
	ie_status_w	:= 'S';
	end;
elsif (dt_interrupcao_p IS NOT NULL AND dt_interrupcao_p::text <> '') then
	ie_status_w	:= 'T';	
end if;

if	((ie_param673_p = 'S') and (ie_solucao_especial_p = 'S') and (ie_status_w = 'A')) then

	if (ie_status_sol_w in ('INT', 'II')) then
		ie_status_w	:= 'T';	
	elsif (ie_status_sol_w <> 'T') then
		ie_status_w	:= 'I';	
	end if;
	
end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
