CREATE OR REPLACE FUNCTION obter_status_hor_sol_processo ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


nr_processo_w	bigint;
ie_status_w	varchar(15) := 'N';


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_processo_w
	from	adep_processo
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p
	and	nr_etapa	= nr_etapa_p
	and	coalesce(dt_cancelamento::text, '') = '';

	if (nr_processo_w > 0) then
		begin
		ie_status_w	:= obter_status_processo(nr_processo_w);
		--if	(ie_status_w in ('G','L')) then
		if (ie_status_w = 'G') then
			ie_status_w	:= 'N';
		elsif (ie_status_w = 'L') then
			ie_status_w	:= 'P';
		end if;
		end;
	end if;
	end;
end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
