SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_impr_lote_fornec (nr_seq_lote_fornec_p bigint) RETURNS varchar AS $body$
DECLARE


/*ds_retorno_w:
N - Não impresso
I - Impresso
P - Parcialmente impresso
S - Impressão superou quantidade do lote*/
ds_retorno_w  varchar(1);
qt_material_w  bigint;
qt_impressa_w  bigint;
dt_impressao_w  timestamp;


BEGIN

select qt_material,
 dt_impressao
into STRICT qt_material_w,
 dt_impressao_w
from material_lote_fornec
where nr_sequencia = nr_seq_lote_fornec_p;

select sum(qt_impressao)
into STRICT qt_impressa_w
from material_lote_fornec_imp
where nr_seq_lote_fornec = nr_seq_lote_fornec_p;

if (coalesce(qt_impressa_w,0) = 0 and coalesce(dt_impressao_w::text, '') = '') then
 ds_retorno_w := 'N';
elsif  ((coalesce(qt_impressa_w,0) = qt_material_w) or (coalesce(qt_impressa_w,0) = 0 and (dt_impressao_w IS NOT NULL AND dt_impressao_w::text <> ''))) then
 ds_retorno_w := 'I';
elsif (coalesce(qt_impressa_w,0) > 0 and coalesce(qt_impressa_w,0) < qt_material_w) then
 ds_retorno_w := 'P';
elsif (coalesce(qt_impressa_w,0) > qt_material_w) then
 ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
