SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_intervencao ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w			varchar(1) := 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and ((coalesce(nr_sequencia_p,nr_seq_horario_p) IS NOT NULL AND (coalesce(nr_sequencia_p,nr_seq_horario_p))::text <> '')) then

	select	coalesce(max(ie_suspenso),'N')
	into STRICT	ie_status_w
	from	pe_prescr_proc
	where	nr_seq_prescr = nr_prescricao_p
	and	nr_sequencia = nr_sequencia_p;

	if (ie_status_w = 'N') and (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
		select	CASE WHEN coalesce(max(a.ie_alteracao),0)=55 THEN 'Z'  ELSE 'N' END
		into STRICT	ie_status_w
		from	prescr_mat_alteracao a
		where	a.nr_prescricao = nr_prescricao_p
		and	a.nr_seq_horario_sae = nr_seq_horario_p
		and	coalesce(a.ie_evento_valido,'S') = 'S'
		and	ie_tipo_item = 'E'
		and	a.ie_alteracao <> 57
		and	((a.ie_alteracao <> 56) or
			 not exists ( 	SELECT		1
					 from		prescr_mat_alteracao b
					 where		a.nr_prescricao = b.nr_prescricao
					 and		a.nr_seq_horario_sae = b.nr_seq_horario_sae
					 and		a.cd_item = b.cd_item
					 and		b.ie_alteracao in (4)
					 and		b.nr_sequencia > a.nr_sequencia));
	end if;
end if;

if (ie_status_w = 'N') then
	ie_status_w	:= null;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
