CREATE OR REPLACE FUNCTION obter_status_isolamento_ocup (cd_estabelecimento_p bigint, nr_atendimento_p bigint, ie_acompanhante_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_isolamento_w		varchar(01);
ie_acomp_w		varchar(01);


BEGIN

ie_acomp_w := Obter_Param_Usuario(44, 57, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_acomp_w);

select	coalesce(max(c.ie_paciente_isolado),'N')
into STRICT	ie_isolamento_w
from 	atendimento_paciente c
where 	c.nr_atendimento 	= nr_atendimento_p;

if (ie_acompanhante_p	= 'S') and (ie_acomp_w		= 'N') then
	ie_isolamento_w		:= 'N';
end if;

return	ie_isolamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
