SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_item_prescr (nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


qt_adm_w	bigint;
ie_suspenso_w	varchar(1);
ie_status_w	varchar(2);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then
	if (ie_tipo_item_p in ('M','S')) then
		select	coalesce(max(ie_suspenso),'N')
		into STRICT	ie_suspenso_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_item_p;

		ie_status_w := ie_suspenso_w;

		if (ie_suspenso_w <> 'S') then
			select	coalesce(count(*),0)
			into STRICT	qt_adm_w
			from	prescr_mat_hor
			where	nr_prescricao = nr_prescricao_p
			and	nr_seq_material = nr_seq_item_p
			and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
			and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

			if (qt_adm_w > 0) then
				ie_status_w := 'HA';
			end if;
		end if;
	elsif (ie_tipo_item_p = 'P') then
		select	coalesce(max(ie_suspenso),'N')
		into STRICT	ie_suspenso_w
		from	prescr_procedimento
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia = nr_seq_item_p;

		ie_status_w := ie_suspenso_w;

		if (ie_suspenso_w <> 'S') then
			select	coalesce(count(*),0)
			into STRICT	qt_adm_w
			from	prescr_proc_hor
			where	nr_prescricao = nr_prescricao_p
			and	nr_seq_procedimento = nr_seq_item_p
			and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
			and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

			if (coalesce(qt_adm_w,0) > 0) then
				ie_status_w := 'HA';
			end if;
		end if;
	elsif (ie_tipo_item_p = 'R') then
		select	coalesce(max(ie_suspenso),'N')
		into STRICT	ie_suspenso_w
		from	prescr_recomendacao
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_item_p;

		ie_status_w := ie_suspenso_w;

		if (ie_suspenso_w <> 'S') then
			select	coalesce(count(*),0)
			into STRICT	qt_adm_w
			from	prescr_rec_hor
			where	nr_prescricao		= nr_prescricao_p
			and	nr_seq_recomendacao	= nr_seq_item_p
			and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
			and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

			if (coalesce(qt_adm_w,0) > 0) then
				ie_status_w := 'HA';
			end if;
		end if;

	elsif (ie_tipo_item_p = 'E') then
		select	coalesce(max(ie_suspenso),'N')
		into STRICT	ie_suspenso_w
		from	pe_prescr_proc
		where	nr_seq_prescr	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_item_p;

		ie_status_w := ie_suspenso_w;

		if (ie_suspenso_w <> 'S') then
			select	coalesce(count(*),0)
			into STRICT	qt_adm_w
			from	pe_prescr_proc_hor
			where	nr_seq_pe_prescr	= nr_prescricao_p
			and	nr_seq_pe_proc	= nr_seq_item_p
			and	(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '');

			if (coalesce(qt_adm_w,0) > 0) then
				ie_status_w := 'HA';
			end if;
		end if;
	end if;
end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
