CREATE OR REPLACE FUNCTION obter_status_item_tab_preco ( cd_material_p bigint, cd_tabela_p bigint, cd_estabelecimento_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_status_item_w	varchar(10) := '';

C01 CURSOR FOR
	SELECT	ie_status_item
