SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_item_transf ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w	varchar(255) := wheb_mensagem_pck.get_texto(309385); -- Pendente
qt_atendida_w	double precision;
qt_material_w	double precision;
dt_cancelamento_w ordem_compra_item_entrega.dt_cancelamento%type;


BEGIN
if (nr_ordem_compra_p IS NOT NULL AND nr_ordem_compra_p::text <> '') and (nr_item_oci_p IS NOT NULL AND nr_item_oci_p::text <> '') then
	begin
	qt_atendida_w := coalesce(obter_qt_oci_trans_nota(nr_ordem_compra_p, nr_item_oci_p,'S'),0);

	select 	coalesce(sum(c.qt_prevista_entrega),0),
            max(c.dt_cancelamento)
	into STRICT	qt_material_w,
            dt_cancelamento_w
	from 	ordem_compra a,
		ordem_compra_item b,
		ordem_compra_item_entrega c
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	b.nr_item_oci = c.nr_item_oci
	and	b.nr_ordem_compra = c.nr_ordem_compra
	and	a.nr_ordem_compra = nr_ordem_compra_p
	and	b.nr_item_oci	  = nr_item_oci_p;

        if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
            ie_status_w	:= wheb_mensagem_pck.get_texto(254986); -- Cancelado 
        else if (qt_material_w <= qt_atendida_w) then
            ie_status_w	:= wheb_mensagem_pck.get_texto(309384); -- Atendido
        elsif (qt_atendida_w = 0) then
            ie_status_w	:= wheb_mensagem_pck.get_texto(309385); -- Pendente
        elsif (qt_material_w > qt_atendida_w) then
            ie_status_w	:= wheb_mensagem_pck.get_texto(309386); -- Parcialmente atendido
        end if;
    end if;
	end;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
