SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_laudo_agenda ( nr_seq_agenda_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w		varchar(3) := 'N';
nr_sequencia_w	laudo_paciente.nr_sequencia%type;
dt_liberacao_w	timestamp;


BEGIN


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	laudo_paciente a
	where	nr_atendimento	= nr_atendimento_p
	and	nr_prescricao		=
		(SELECT max(a.nr_prescricao)
		from	prescr_procedimento b,
			prescr_medica a
		where	a.nr_prescricao	= b.nr_prescricao
		and	a.nr_atendimento	= nr_atendimento_p
		and	b.cd_procedimento	= 
			(select cd_procedimento
			from agenda_paciente
			where	nr_sequencia	= nr_seq_agenda_p));
	select	max(dt_liberacao)
	into STRICT	dt_liberacao_w
	from	laudo_paciente
	where	nr_sequencia	= nr_sequencia_w;

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ie_status_w		:= 'LL';
	elsif (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		ie_status_w		:= 'LNL';
	end if;	
	end;
end if;

return ie_status_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
