CREATE OR REPLACE FUNCTION obter_status_liberacao (dt_liberacao_p timestamp, nr_seq_assinatura_p bigint, dt_inativacao_p timestamp, ie_situacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(3);


BEGIN

if (coalesce(dt_liberacao_p::text, '') = '') then
	ds_retorno_w	:= 'PL';
elsif (ie_situacao_p = 'I') or (dt_inativacao_p IS NOT NULL AND dt_inativacao_p::text <> '') then
	ds_retorno_w	:= 'I';
elsif (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') and (coalesce(nr_seq_assinatura_p::text, '') = '') then
	ds_retorno_w	:= 'PA';
end if;

return	ds_retorno_w;

end;
