CREATE OR REPLACE FUNCTION obter_status_lote_contabil ( nr_lote_contabil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_w				varchar(255);
dt_integracao_w				timestamp;
dt_consistencia_w			timestamp;
dt_atualizacao_w			timestamp;


BEGIN

begin
select	dt_atualizacao_saldo,
	dt_consistencia,
	dt_integracao
into STRICT	dt_atualizacao_w,
	dt_consistencia_w,
	dt_integracao_w
from 	Lote_Contabil
where nr_lote_contabil	= nr_lote_contabil_p;
exception when others then
	dt_atualizacao_w	:= null;
	dt_consistencia_w	:= null;
	dt_integracao_w		:= null;
end;

if (dt_atualizacao_w IS NOT NULL AND dt_atualizacao_w::text <> '') then
	ds_status_w		:= substr(wheb_mensagem_pck.get_texto(339848),1,255); /*:= 'Atualizado';*/
elsif (dt_consistencia_w IS NOT NULL AND dt_consistencia_w::text <> '') then
	ds_status_w		:= substr(wheb_mensagem_pck.get_texto(339849),1,255);/*:= 'Consistido';*/
elsif (dt_integracao_w IS NOT NULL AND dt_integracao_w::text <> '') then
	ds_status_w		:= substr(wheb_mensagem_pck.get_texto(339850),1,255);/*:= 'Integrado';*/
else
	ds_status_w		:= substr(wheb_mensagem_pck.get_texto(339851),1,255);/*:= 'Digitado';*/
end if;

return ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
