CREATE OR REPLACE FUNCTION obter_status_material_html5 ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_situacao_w	varchar(20);

BEGIN

begin
select CASE WHEN ie_situacao='I' THEN substr(obter_desc_expressao_idioma(687938,null,wheb_usuario_pck.get_nr_seq_idioma),1,20)  ELSE substr(obter_desc_expressao_idioma(283906,null,wheb_usuario_pck.get_nr_seq_idioma),1,20) END
into STRICT	ds_situacao_w
from	material
where	cd_material	= cd_material_p;
exception
	when others then
		ds_situacao_w	:= substr(obter_desc_expressao_idioma(283906,null,wheb_usuario_pck.get_nr_seq_idioma),1,20);
end;

return ds_situacao_w;

end;
$body$
