SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_medicamento_ccih (nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

IF (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') AND (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') THEN

	-- reprovado
	SELECT	MAX(obter_desc_expressao(331541)/*'Reprovado'*/
)
	INTO STRICT	ds_retorno_w
	FROM	prescr_material a
	WHERE	a.nr_prescricao =  nr_prescricao_p
	AND	a.cd_material   =  nr_sequencia_p
	AND	a.qt_dias_liberado = 0
	AND	a.qt_dias_solicitado > a.qt_dias_liberado;

	-- parcial
	IF (coalesce(ds_retorno_w::text, '') = '') THEN
		SELECT	MAX(obter_desc_expressao(295282)/*'Parcial'*/
)
		INTO STRICT	ds_retorno_w
		FROM	prescr_material a
		WHERE	a.nr_prescricao = nr_prescricao_p
		AND	a.cd_material   = nr_sequencia_p
		AND	a.qt_dias_liberado > 0
		AND	a.qt_dias_solicitado > a.qt_dias_liberado;

		--liberado
		IF (coalesce(ds_retorno_w::text, '') = '') THEN
			SELECT MAX(obter_desc_expressao(292549)/*'Liberado'*/
)
			INTO STRICT   ds_retorno_w
			FROM   prescr_material a
			WHERE  a.nr_prescricao = nr_prescricao_p
			AND    a.cd_material   = nr_sequencia_p
			AND    a.qt_dias_solicitado = a.qt_dias_liberado;

			-- pendente de liberacao
			IF (coalesce(ds_retorno_w::text, '') = '') THEN
				SELECT	MAX(obter_desc_expressao(295406)/*'Pendente'*/
)
				INTO STRICT	ds_retorno_w
				FROM	prescr_material a
				WHERE	a.nr_prescricao = nr_prescricao_p
				AND	a.cd_material   = nr_sequencia_p
				AND	coalesce(a.qt_dias_liberado::text, '') = ''
				AND	(a.qt_dias_solicitado IS NOT NULL AND a.qt_dias_solicitado::text <> '');
			END IF;
		END IF;
	END IF;
END IF;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_medicamento_ccih (nr_prescricao_p bigint, nr_sequencia_p bigint) FROM PUBLIC;

