CREATE OR REPLACE FUNCTION obter_status_medic_dor ( nr_prescricao_p bigint, nr_seq_material_p bigint ) RETURNS varchar AS $body$
DECLARE


dt_horario_w		prescr_mat_hor.dt_horario%type;
dt_suspensao_w		prescr_mat_hor.dt_suspensao%type;
dt_fim_horario_w	prescr_mat_hor.dt_fim_horario%type;
ds_status_w			varchar(25);
ds_espaco_w			varchar(10) := '     ';
ds_retorno_w		varchar(1000) := '';

c01 CURSOR FOR
	SELECT 	a.dt_horario,
			a.dt_suspensao,
			a.dt_fim_horario
	from 	prescr_mat_hor a
	where 	a.nr_prescricao = nr_prescricao_p
	and 	a.nr_seq_material = nr_seq_material_p
	and		coalesce(a.ie_situacao, 'A') = 'A'
	and		coalesce(a.ie_adep, 'S') = 'S'
	and		a.ie_agrupador = 1
	and		((coalesce(a.ie_horario_especial,'N') = 'N') or (a.dt_fim_horario IS NOT NULL AND a.dt_fim_horario::text <> ''))
	order by	a.nr_sequencia;


BEGIN

	open c01;
		loop
			fetch c01 into
				dt_horario_w,
				dt_suspensao_w,
				dt_fim_horario_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
  				begin
					if (dt_horario_w IS NOT NULL AND dt_horario_w::text <> '') then
						ds_status_w := obter_desc_expressao(295406);

						if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
							ds_status_w := obter_desc_expressao(298991);
						elsif (dt_fim_horario_w IS NOT NULL AND dt_fim_horario_w::text <> '') then
							ds_status_w := obter_desc_expressao(321558);
						end if;

						ds_retorno_w := substr(ds_retorno_w || to_char(dt_horario_w, 'dd/mm hh24:mi') || ' ' || substr('(' || ds_status_w || ')' || ds_espaco_w, 1, 15), 1, 1000);

					end if;
				end;
		end loop;
	close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
