CREATE OR REPLACE FUNCTION obter_status_neg_cheque_cr (nr_seq_cheque_p bigint) RETURNS bigint AS $body$
DECLARE

 
/*	0 - Fora de negociação 
	1 - A negociar 
	2 - negociado 
*/
 
 
ie_status_w		smallint;
vl_saldo_negociado_w	double precision;
ie_retorno_w		smallint	:= null;
vl_cheque_w		double precision;


BEGIN 
 
if (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') then 
 
	select	obter_status_cheque(nr_seq_cheque), 
		vl_saldo_negociado, 
		vl_cheque 
	into STRICT	ie_status_w, 
		vl_saldo_negociado_w, 
		vl_cheque_w 
	from	cheque_cr 
	where	nr_seq_cheque	= nr_seq_cheque_p;
 
	/* Se está em nesses status (Posse do hospital), o cheque pode ser negociado */
 
	if (ie_status_w in (1,3,5,10)) then 
 
		/* Se o saldo de negociação é maior que zero é porque parte do cheque já foi negociado */
 
		if (vl_saldo_negociado_w = 0) and (vl_cheque_w <> 0) then 
			ie_retorno_w	:= 2;
		else 
			ie_retorno_w	:= 1;
		end if;
	else 
		ie_retorno_w	:= 0;	
	end if;
 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_neg_cheque_cr (nr_seq_cheque_p bigint) FROM PUBLIC;

