SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_nfe (nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_existe_w		bigint;
nr_seq_protocolo_w	varchar(255);

BEGIN

select 	coalesce(max(nr_nfe_imp_protocolo),'0') nr_protocolo
into STRICT	nr_seq_protocolo_w
from	nota_fiscal
where	(nr_seq_exportada IS NOT NULL AND nr_seq_exportada::text <> '');

select	count(*)
into STRICT	qt_existe_w
from	nota_fiscal a,
	nota_fiscal_hist b
where	a.nr_sequencia 		= b.nr_seq_nota
and	a.nr_nfe_imp_protocolo  = nr_seq_protocolo_w
and	b.cd_evento		= '31';

return	qt_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
