CREATE OR REPLACE FUNCTION obter_status_paciente_painel ( DT_ANALISE_P timestamp, DT_CHEGADA_P timestamp, DT_PAC_AGUARDO_P timestamp, DT_ALTA_P timestamp, DT_PRE_INDUC_P timestamp, DT_SALA_REC_P timestamp, DT_CHEGADA_FIM_P timestamp, DT_SAIDA_CC_P timestamp, IE_STATUS_AGENDA text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2);

/*
SC	- Saída do paciente do centro cirúrgico
CF	- Paciente na sala
SR	- Paciente em recuperação anestésica
PI	- Paciente em pré indução
AL	- Paciente em alta do centro cirúrgico
PA	- Paciente em aguardo
CH	- Paciente chamado
AN	- Em análise
CA  - Cancelada
*/
BEGIN

if (IE_STATUS_AGENDA = 'C') then
	ds_retorno_w := 'CA';
elsif (DT_SAIDA_CC_P IS NOT NULL AND DT_SAIDA_CC_P::text <> '') then
	ds_retorno_w := 'SC';
elsif (DT_ALTA_P IS NOT NULL AND DT_ALTA_P::text <> '') then
	ds_retorno_w := 'AL';
elsif (DT_SALA_REC_P IS NOT NULL AND DT_SALA_REC_P::text <> '') then
	ds_retorno_w := 'SR';
elsif (DT_CHEGADA_FIM_P IS NOT NULL AND DT_CHEGADA_FIM_P::text <> '') then
	ds_retorno_w := 'CF';
elsif (DT_PRE_INDUC_P IS NOT NULL AND DT_PRE_INDUC_P::text <> '') then
	ds_retorno_w := 'PI';
elsif (DT_PAC_AGUARDO_P IS NOT NULL AND DT_PAC_AGUARDO_P::text <> '') then
	ds_retorno_w := 'PA';
elsif (DT_CHEGADA_P IS NOT NULL AND DT_CHEGADA_P::text <> '') then
	ds_retorno_w := 'CH';
elsif (DT_ANALISE_P IS NOT NULL AND DT_ANALISE_P::text <> '') then
	ds_retorno_w := 'AN';
end if;

RETURN	ds_retorno_w;

END;
