SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_paciente_qt3 (nr_seq_atendimento_p bigint, dt_inicio_adm_p timestamp, dt_fim_adm_p timestamp, nr_seq_local_p bigint, ie_exige_lib_p text, dt_chegada_p timestamp, ie_codigo_desc_p text, dt_suspensao_p timestamp, dt_retirada_medicacao_p timestamp, dt_inicio_triagem_p timestamp, dt_fim_triagem_p timestamp, DT_RECEBIMENTO_MEDIC_p timestamp default null) RETURNS varchar AS $body$
DECLARE


/*ie_codigo_desc_p D - Descricao C- Codigo */

ds_status_w			varchar(80);
ie_prioridade_apto_w		varchar(1);
qt_intercorrencia_w		bigint;
qt_total_atend_w		bigint;
qt_total_geral_w		bigint;
nr_prescricao_w			bigint := 0;
dt_prescricao_suspensao_w	timestamp;
dt_aguard_result_exame_w	timestamp;
dt_quimio_liberada_w 		timestamp;
ie_utiliza_legenda_w		varchar(1);
ie_status_agenda_w 		varchar(1);
dt_apto_w			timestamp;
dt_cancelamento_w	timestamp;
dt_interrompido_w	timestamp;
ie_prio_quimio_lib_w	varchar(1);
ie_util_leg_com_atend_w	varchar(1);
ie_possui_atend_w	varchar(1) := 'N';
ie_considera_med_disp_w varchar(2);
qt_motivo_baixa_w	bigint;
dt_confirmada_w		timestamp;
ie_medic_lib_sem_acomodacao_w varchar(1) := 'N';
qt_agendamento_w    bigint := 0;


BEGIN

ie_utiliza_legenda_w := Obter_Param_Usuario(3130, 197, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_utiliza_legenda_w);
ie_prioridade_apto_w := Obter_Param_Usuario(3130, 308, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_prioridade_apto_w);
ie_prio_quimio_lib_w := Obter_Param_Usuario(3130, 320, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_prio_quimio_lib_w);
ie_util_leg_com_atend_w := Obter_Param_Usuario(3130, 425, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_util_leg_com_atend_w);
ie_considera_med_disp_w := Obter_Param_Usuario(3130, 465, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_considera_med_disp_w);
ie_medic_lib_sem_acomodacao_w := Obter_Param_Usuario(3130, 497, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_medic_lib_sem_acomodacao_w);

select 	coalesce(max(nr_prescricao),0)
into STRICT 	nr_prescricao_w
from 	paciente_atendimento
where 	nr_seq_atendimento = nr_seq_atendimento_p;

if (ie_considera_med_disp_w = 'S')	then
	select	count(cd_motivo_baixa)
	into STRICT	qt_motivo_baixa_w
	from	prescr_material
	where 	coalesce(cd_motivo_baixa,0) = 0
	and	ie_agrupador = 1
	and	nr_prescricao = nr_prescricao_w;
end if;

select	dt_aguard_result_exame,
	dt_quimio_liberada,
	nr_prescricao,
	dt_apto,
	dt_cancelamento,
	dt_interrompido,
	substr(Qt_Obter_Status_Agenda(nr_seq_atendimento),1,50),
	CASE WHEN coalesce(nr_atendimento::text, '') = '' THEN  'N'  ELSE 'S' END ,
	obter_data_confirm_quimio(nr_seq_atendimento)
into STRICT	dt_aguard_result_exame_w,
	dt_quimio_liberada_w,
	nr_prescricao_w,
	dt_apto_w,
	dt_cancelamento_w,
	dt_interrompido_w,
	ie_status_agenda_w,
	ie_possui_atend_w,
	dt_confirmada_w
from 	paciente_atendimento
where 	nr_seq_atendimento = nr_seq_atendimento_p;

if (ie_exige_lib_p = 'S') then
	ds_status_w		:= '357';
elsif (dt_fim_adm_p IS NOT NULL AND dt_fim_adm_p::text <> '') then
	ds_status_w		:= '83';
else
	begin
	select	count(*)
	into STRICT	qt_intercorrencia_w
	from	paciente_atend_interc
	where	nr_seq_atendimento	= nr_seq_atendimento_p
	and	(dt_intercorrencia IS NOT NULL AND dt_intercorrencia::text <> '')
	and	coalesce(dt_fim_intercorrencia::text, '') = '';

    select	count(*)
    into STRICT    qt_agendamento_w
    from	agenda_quimio
    where	nr_seq_atendimento = nr_seq_atendimento_p;

	if (qt_intercorrencia_w > 0) then
		ds_status_w		:= '82';
	elsif (dt_quimio_liberada_w IS NOT NULL AND dt_quimio_liberada_w::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and (ie_prio_quimio_lib_w = 'E') then /*OS 1334593*/
			ds_status_w		:= '1953';
	elsif (dt_inicio_adm_p IS NOT NULL AND dt_inicio_adm_p::text <> '') then
		ds_status_w		:= '81';
	else
		begin
		select	count(*)
		into STRICT	qt_total_atend_w
		from	can_ordem_prod
		where	(dt_entrega_setor IS NOT NULL AND dt_entrega_setor::text <> '')
		and	nr_seq_atendimento	 = nr_seq_atendimento_p;

		select	count(*)
		into STRICT	qt_total_geral_w
		from	can_ordem_prod
		where	nr_seq_atendimento	 = nr_seq_atendimento_p;

		if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
			ds_status_w := '3670';
		elsif (dt_interrompido_w IS NOT NULL AND dt_interrompido_w::text <> '') then
			ds_status_w := '3681';
		elsif	((dt_chegada_p IS NOT NULL AND dt_chegada_p::text <> '') and
			((nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') or (ie_medic_lib_sem_acomodacao_w = 'S')) and (qt_total_atend_w <= qt_total_geral_w) and (coalesce(dt_recebimento_medic_p::text, '') = '') and (qt_total_geral_w > 0 ) and (qt_total_atend_w > 0)) or ((nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') and
			qt_motivo_baixa_w = 0 and
			ie_considera_med_disp_w = 'S') then
			ds_status_w 		:= '524';
		elsif (dt_retirada_medicacao_p IS NOT NULL AND dt_retirada_medicacao_p::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') then
			ds_status_w		:= '1473';
		elsif (dt_quimio_liberada_w IS NOT NULL AND dt_quimio_liberada_w::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and (ie_prio_quimio_lib_w = 'S') then
			ds_status_w		:= '1953';
		elsif (ie_prioridade_apto_w = 'N') and (dt_apto_w IS NOT NULL AND dt_apto_w::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and (coalesce(dt_chegada_p::text, '') = '') then
			ds_status_w		:= '2762';
		elsif (ie_prioridade_apto_w = 'S') and (dt_apto_w IS NOT NULL AND dt_apto_w::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and (dt_chegada_p IS NOT NULL AND dt_chegada_p::text <> '') then
			ds_status_w		:= '2762';
		elsif (ie_utiliza_legenda_w = 'S') and (dt_chegada_p IS NOT NULL AND dt_chegada_p::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '')  and (coalesce(nr_prescricao_w::text, '') = '') then
			ds_status_w		:= '2621';
		elsif (dt_chegada_p IS NOT NULL AND dt_chegada_p::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '')  then
			ds_status_w		:= '214';
		elsif (dt_quimio_liberada_w IS NOT NULL AND dt_quimio_liberada_w::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') then
			ds_status_w		:= '1953';
		elsif (dt_aguard_result_exame_w IS NOT NULL AND dt_aguard_result_exame_w::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') then
			ds_status_w		:= '1951';
		elsif (dt_chegada_p IS NOT NULL AND dt_chegada_p::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') and
			((coalesce(nr_seq_local_p::text, '') = '') and (ie_medic_lib_sem_acomodacao_w <> 'S'))    then
			ds_status_w		:= '80';
		elsif (dt_fim_triagem_p IS NOT NULL AND dt_fim_triagem_p::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') then
			ds_status_w		:= '1435';
		elsif (dt_inicio_triagem_p IS NOT NULL AND dt_inicio_triagem_p::text <> '') and (coalesce(dt_recebimento_medic_p::text, '') = '') then
			ds_status_w		:= '1434';
		elsif (ie_status_agenda_w = 'F') then
			ds_status_w		:= '4273';
        elsif (DT_RECEBIMENTO_MEDIC_p IS NOT NULL AND DT_RECEBIMENTO_MEDIC_p::text <> '') then
			ds_status_w		:= '2334';
		elsif (dt_confirmada_w IS NOT NULL AND dt_confirmada_w::text <> '') then
			ds_status_w		:= '5530';	
		elsif (qt_agendamento_w > 0) then
            ds_status_w		:= '76';
            	elsif (ie_util_leg_com_atend_w = 'S') and (ie_possui_atend_w = 'S')  then
			ds_status_w		:= '4517';
        else
			ds_status_w		:= '11137';
		end if;
		end;
	end if;
	end;
end if;

if (nr_prescricao_w > 0) then

	select 	max(dt_suspensao)
	into STRICT	dt_prescricao_suspensao_w
	from 	prescr_medica
	where 	nr_prescricao = nr_prescricao_w;

	if (dt_prescricao_suspensao_w IS NOT NULL AND dt_prescricao_suspensao_w::text <> '') then
		ds_status_w	:= '1334';
	end if;

end if;


if (dt_suspensao_p IS NOT NULL AND dt_suspensao_p::text <> '') then
	ds_status_w := '3323';
end if;

if (ie_codigo_desc_p = 'D') then
	ds_status_w	:= obter_valor_dominio(1242, ds_status_w);
end if;

return ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_paciente_qt3 (nr_seq_atendimento_p bigint, dt_inicio_adm_p timestamp, dt_fim_adm_p timestamp, nr_seq_local_p bigint, ie_exige_lib_p text, dt_chegada_p timestamp, ie_codigo_desc_p text, dt_suspensao_p timestamp, dt_retirada_medicacao_p timestamp, dt_inicio_triagem_p timestamp, dt_fim_triagem_p timestamp, DT_RECEBIMENTO_MEDIC_p timestamp default null) FROM PUBLIC;

