SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_paciente_qt4 (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE






ds_status_w		varchar(80);
qt_intercorrencia_w	bigint;
qt_total_atend_w	bigint;
qt_total_geral_w	bigint;
nr_prescricao_w		bigint := 0;
dt_prescricao_suspensao_w	timestamp;
ie_util_leg_com_atend_w	varchar(1);
ie_possui_atend_w	varchar(1) := 'N';
dt_inicio_adm_w		timestamp;
dt_fim_adm_w		timestamp;
nr_seq_local_w		bigint;
ie_exige_lib_w		varchar(1);
dt_chegada_w		timestamp;
dt_suspensao_w		timestamp;


BEGIN

select 	max(dt_inicio_adm),
	max(dt_fim_adm),
	max(nr_seq_local),
	max(ie_exige_liberacao),
	max(dt_chegada),
	max(dt_suspensao)
into STRICT 	dt_inicio_adm_w,
	dt_fim_adm_w,
	nr_seq_local_w,
	ie_exige_lib_w,
	dt_chegada_w,
	dt_suspensao_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;


ie_util_leg_com_atend_w := Obter_Param_Usuario(3130, 425, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_util_leg_com_atend_w);

if (ie_exige_lib_w = 'S') then
	ds_status_w		:= '357';
elsif (dt_fim_adm_w IS NOT NULL AND dt_fim_adm_w::text <> '') then
	ds_status_w		:= '83';
else
	begin
	select	count(*)
	into STRICT	qt_intercorrencia_w
	from	paciente_atend_interc
	where	nr_seq_atendimento	= nr_seq_atendimento_p
	and	(dt_intercorrencia IS NOT NULL AND dt_intercorrencia::text <> '')
	and	coalesce(dt_fim_intercorrencia::text, '') = '';

	if (qt_intercorrencia_w > 0) then
		ds_status_w		:= '82';
	elsif (dt_inicio_adm_w IS NOT NULL AND dt_inicio_adm_w::text <> '') then
		ds_status_w		:= '81';
	else
		begin
		select	count(*)
		into STRICT	qt_total_atend_w
		from	can_ordem_prod
		where	(dt_entrega_setor IS NOT NULL AND dt_entrega_setor::text <> '')
		and	nr_seq_atendimento	 = nr_seq_atendimento_p;

		select	count(*)
		into STRICT	qt_total_geral_w
		from	can_ordem_prod
		where	nr_seq_atendimento	 = nr_seq_atendimento_p;

		select	coalesce(max('S'),'N')
		into STRICT	ie_possui_atend_w
		from	paciente_atendimento
		where	nr_seq_atendimento	= nr_seq_atendimento_p
		and	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');

		if (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') and (nr_seq_local_w IS NOT NULL AND nr_seq_local_w::text <> '')  then
			ds_status_w		:= '214';
		elsif (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') and (coalesce(nr_seq_local_w::text, '') = '')  then
			ds_status_w		:= '80';
		elsif (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') and (nr_seq_local_w IS NOT NULL AND nr_seq_local_w::text <> '') and (qt_total_atend_w <= qt_total_geral_w) and (qt_total_geral_w > 0 ) and (qt_total_atend_w > 0) then
			ds_status_w 		:= '524';
		elsif (ie_util_leg_com_atend_w = 'S') and (ie_possui_atend_w = 'S')  then
			ds_status_w		:= '4517';
		else
			ds_status_w		:= '76';
		end if;
		end;
	end if;
	end;
end if;

select 	coalesce(max(nr_prescricao),0)
into STRICT 	nr_prescricao_w
from 	paciente_atendimento
where 	nr_seq_atendimento = nr_seq_atendimento_p;

if (nr_prescricao_w > 0) then

	select 	max(dt_suspensao)
	into STRICT	dt_prescricao_suspensao_w
	from 	prescr_medica
	where 	nr_prescricao = nr_prescricao_w;

	if (dt_prescricao_suspensao_w IS NOT NULL AND dt_prescricao_suspensao_w::text <> '') then
		ds_status_w	:= '1334';
	end if;

end if;


if (dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') then
	ds_status_w	:= '1319';
end if;


return ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_paciente_qt4 (nr_seq_atendimento_p bigint) FROM PUBLIC;

