SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_pac_ocup ( nr_seq_atendimento_p text) RETURNS varchar AS $body$
DECLARE


/*
	AG - Agendado;
	EA - Espera Acomodacao
	EQ - Espera Quimioterapia
	MQ - Em Quimioterapia
	AQ - Alta Quimioterapia
	AT - Atrasado


*/
ds_status_paciente_w	varchar(255);
dt_fim_adm_w		timestamp;
dt_inicio_adm_w		timestamp;
dt_chegada_w		timestamp;
dt_recebimento_w	timestamp;
nr_seq_local_w		bigint;
dt_recebimento_medic_w	timestamp;
dt_prevista_w 		timestamp;




BEGIN
ds_status_paciente_w := 'AG';


select	max(dt_fim_adm),
	max(dt_inicio_adm),
	max(dt_chegada),
	max(dt_recebimento_medic),
	max(nr_seq_local),
	max(dt_prevista)
into STRICT	dt_fim_adm_w,
	dt_inicio_adm_w,
	dt_chegada_w,
	dt_recebimento_medic_w,
	nr_seq_local_w,
	dt_prevista_w
from	paciente_atendimento
where 	nr_seq_atendimento = nr_seq_atendimento_p;


if (dt_fim_adm_w IS NOT NULL AND dt_fim_adm_w::text <> '') then
	ds_status_paciente_w		:= 'AQ';
elsif   ((dt_inicio_adm_w) > (dt_prevista_w)) then
	ds_status_paciente_w		:= 'AT';
elsif (dt_inicio_adm_w IS NOT NULL AND dt_inicio_adm_w::text <> '') then
	ds_status_paciente_w		:= 'MQ';
elsif (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') and (coalesce(dt_recebimento_medic_w::text, '') = '') and (nr_seq_local_w IS NOT NULL AND nr_seq_local_w::text <> '')  then
	ds_status_paciente_w		:= 'EQ';
elsif (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') and (coalesce(dt_recebimento_medic_w::text, '') = '') and (coalesce(nr_seq_local_w::text, '') = '')  then
	ds_status_paciente_w := 'EA';
end if;


return	ds_status_paciente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
