CREATE OR REPLACE FUNCTION obter_status_pac_proc (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ie_status_w  varchar(1);

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	coalesce(max('A'),'I')
	into STRICT 	ie_status_w
	from 	paciente_setor
	where 	ie_status = 'A'
	and 	cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
