SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_painel_pa (nr_seq_local_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_controle_w		varchar(1);
ie_status_w		varchar(10);
qt_itens_w		bigint;
nr_atendimento_w	bigint;
ie_sexo_w		varchar(1);
ie_faixa_etaria_w	varchar(15);


BEGIN

select 	max(ie_controle)
into STRICT	ie_controle_w
from	pa_local a,
	pa_grupo_local b
where	a.nr_seq_grupo_pa = b.nr_sequencia
and	a.nr_sequencia = nr_seq_local_p;


select 	count(*),
	max(nr_atendimento)
into STRICT	qt_itens_w,
	nr_atendimento_w
from	historico_localizacao_pa a
where	nr_seq_local_destino_pa = nr_seq_local_p
and	coalesce(dt_saida_local::text, '') = ''
and	nr_atendimento = nr_atendimento_p;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	max(ie_sexo),
		max(Obter_classif_etaria(a.cd_estabelecimento,b.cd_pessoa_fisica,'C'))
	into STRICT	ie_sexo_w,
		ie_faixa_etaria_w
	from	atendimento_paciente a,
		pessoa_fisica b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	a.nr_atendimento = nr_atendimento_p;
end if;
ie_status_w := 'CL';

if (coalesce(nr_atendimento_p::text, '') = '') then
	ie_status_w := 'L';
else
	if (ie_sexo_w = 'M') then

		if (ie_faixa_etaria_w = 'A') then
			ie_status_w := 'MA';
		elsif (ie_faixa_etaria_w = 'C') then
			ie_status_w := 'MIN';
		elsif (ie_faixa_etaria_w = 'I') then
			ie_status_w := 'MID';
		elsif (ie_faixa_etaria_w = 'L') then
			ie_status_w := 'MAD';
		end if;
	elsif (ie_sexo_w = 'F') then
		if (ie_faixa_etaria_w = 'A') then
			ie_status_w := 'FA';
		elsif (ie_faixa_etaria_w = 'C') then
			ie_status_w := 'FIN';
		elsif (ie_faixa_etaria_w = 'I') then
			ie_status_w := 'FID';
		elsif (ie_faixa_etaria_w = 'L') then
			ie_status_w := 'FAD';
		end if;
	end if;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
