SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_painel (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(255);

ie_status_agenda_w	varchar(3);
ie_carater_cirurgia_w	varchar(3);
dt_saida_paciente_cc_w  	timestamp;
dt_alta_cc_w		timestamp;
dt_sala_recuperacao_w	timestamp;
dt_chegada_fim_w		timestamp;
dt_chamada_pre_inducao_w timestamp;
dt_paciente_aguardo_w	timestamp;
dt_chegada_w		timestamp;
dt_analise_w		timestamp;
nr_cirurgia_w		bigint;
ie_status_cirurgia_w	varchar(3);


BEGIN
select 	max(ie_status_agenda),
            	max(ie_carater_cirurgia),
            	max(dt_saida_paciente_cc),
            	max(dt_alta_cc),
            	max(dt_sala_recuperacao),
            	max(dt_chegada_fim),
            	max(dt_chamada_pre_inducao),
            	max(dt_paciente_aguardo),
            	max(dt_chegada),
            	max(dt_analise),
            	max(nr_cirurgia)
into STRICT       	ie_status_agenda_w,
            	ie_carater_cirurgia_w,
            	dt_saida_paciente_cc_w,
            	dt_alta_cc_w,
            	dt_sala_recuperacao_w,
            	dt_chegada_fim_w,
            	dt_chamada_pre_inducao_w,
            	dt_paciente_aguardo_w,
            	dt_chegada_w,
            	dt_analise_w,
            	nr_cirurgia_w
from	agenda_paciente
where 	nr_sequencia = nr_sequencia_p;

if (coalesce(Obter_Valor_Param_Usuario(871, 822, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario,  wheb_usuario_pck.get_cd_estabelecimento),'N') = 'S') then
      	SELECT   max(SUBSTR(obter_desc_expressao(cd_exp_item),1,254))
       	INTO STRICT      ds_retorno_w
       	FROM     TASY_PADRAO_COR
       	WHERE   nr_sequencia = obter_proximo_status_painel(nr_sequencia_p,nr_cirurgia_w);

else
               if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then
                  SELECT MAX(ie_status_cirurgia)
                  into STRICT   ie_status_cirurgia_w
                  FROM   cirurgia
                  WHERE  nr_cirurgia = nr_cirurgia_w;
               end if;

               if (ie_status_agenda_w = 'C') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309479); -- Cancelada
               elsif (ie_status_cirurgia_w = 4) then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309480); -- Interrompida
               elsif (ie_carater_cirurgia_w = 'U') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309481); -- Urgência
               elsif (dt_saida_paciente_cc_w IS NOT NULL AND dt_saida_paciente_cc_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309482); -- Saída do pac do CC
               elsif (dt_alta_cc_w IS NOT NULL AND dt_alta_cc_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309483); -- Paciente em Alta CC
               elsif (dt_sala_recuperacao_w IS NOT NULL AND dt_sala_recuperacao_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309484); -- Paciente em RA
               elsif (dt_chegada_fim_w IS NOT NULL AND dt_chegada_fim_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309485); -- Paciente em sala
               elsif (dt_chamada_pre_inducao_w IS NOT NULL AND dt_chamada_pre_inducao_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309486); -- Pac em pré indução
               elsif (dt_paciente_aguardo_w IS NOT NULL AND dt_paciente_aguardo_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309487); -- Paciente em aguardo
               elsif (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309488); -- Paciente chamado
               elsif (dt_analise_w IS NOT NULL AND dt_analise_w::text <> '') then
                  ds_retorno_w := wheb_mensagem_pck.get_texto(309489); -- Em análise
               end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
