SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_parecer ( nr_parecer_p bigint) RETURNS varchar AS $body$
DECLARE


			
ie_retorno_w	varchar(10);
qt_reg_w	bigint;

			

BEGIN

select	count(1)
into STRICT	qt_reg_w
from	parecer_medico  a
where	a.nr_parecer	= nr_parecer_p
and	coalesce(a.dt_inativacao::text, '') = ''
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

if (qt_reg_w	= 0) then
	ie_retorno_w	:= 'P';
else
	select	count(1)
	into STRICT	qt_reg_w
	from	parecer_medico  a
	where	a.nr_parecer	= nr_parecer_p
	and	coalesce(a.dt_inativacao::text, '') = ''
	and (a.IE_STATUS  = 'A')
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
	
	
	if (qt_reg_w	> 0) then
		ie_retorno_w	:= 'A';
	else
	
		select	count(1)
		into STRICT	qt_reg_w
		from	parecer_medico  a
		where	a.nr_parecer	= nr_parecer_p
		and	coalesce(a.dt_inativacao::text, '') = ''
		and (a.IE_STATUS  = 'F')
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
		
		if (qt_reg_w	> 0) then
			ie_retorno_w	:= 'F';
		end if;
	
	end if;
	
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_parecer ( nr_parecer_p bigint) FROM PUBLIC;

