CREATE OR REPLACE FUNCTION obter_status_problema ( ie_status_p bigint ) RETURNS varchar AS $body$
DECLARE

  ds_status_w varchar(255);

BEGIN
  SELECT ds
  INTO STRICT ds_status_w
  FROM (SELECT (vl_dominio)::numeric  cd,
      ds_valor_dominio ds
    FROM valor_dominio_v v
    WHERE cd_dominio = 8221

UNION ALL

    SELECT nr_sequencia cd,
      ds_status ds
    FROM status_problema    
    ORDER BY ds
    ) alias1
  WHERE cd = ie_status_p;
  RETURN ds_status_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
