SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_produto_opme (nr_lote_producao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
 /* S - status do lote
     C - Cor */
DECLARE


nr_seq_prioridade_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

if (nr_lote_producao_p IS NOT NULL AND nr_lote_producao_p::text <> '') then
	select  max(a.nr_seq_prioridade)
	into STRICT	nr_seq_prioridade_w
	from    lote_prod_comp_estagio a,
		lote_producao_comp b
	where   a.nr_sequencia = b.nr_seq_estagio
	and     b.nr_lote_producao = nr_lote_producao_p;
end if;

if (nr_seq_prioridade_w IS NOT NULL AND nr_seq_prioridade_w::text <> '') and (ie_opcao_p = 'S') then
	select	max(ds_status_lote)
	into STRICT	ds_retorno_w
	from	lote_prod_comp_estagio
	where	nr_seq_prioridade = nr_seq_prioridade_w;

elsif (nr_seq_prioridade_w IS NOT NULL AND nr_seq_prioridade_w::text <> '') and (ie_opcao_p = 'C') then
	select	max(ds_cor)
	into STRICT	ds_retorno_w
	from	lote_prod_comp_estagio
	where	nr_seq_prioridade = nr_seq_prioridade_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
