SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_projeto_npi (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(5);
qt_cronograma_w		integer;
qt_aprovacao_w		integer;
qt_crono_nao_fin_w	bigint;

/*
S = Status do projeto
*/
BEGIN

select	count(*)
into STRICT	qt_cronograma_w
from	proj_cronograma e
where	e.nr_seq_proj = nr_sequencia_p
and	ie_classificacao = 'N';

if (qt_cronograma_w > 0) then

	select	count(*)
	into STRICT	qt_aprovacao_w
	from	proj_cronograma e
	where	coalesce(e.dt_aprovacao::text, '') = ''
	and	e.nr_seq_proj = nr_sequencia_p;

	if (qt_aprovacao_w > 0) then
		retorno_w := '2';
	else
		select	count(c.nr_sequencia)
		into STRICT	qt_crono_nao_fin_w
		from	proj_cronograma b,
			proj_cron_etapa c
		where	b.nr_seq_proj = nr_sequencia_p
		and	c.nr_seq_cronograma = b.nr_sequencia
		and	c.pr_etapa < 100;

		if qt_crono_nao_fin_w = 0 then
			retorno_w := '3';
		end if;
	end if;
else
	retorno_w:= '1';
end if;


return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_status_projeto_npi (nr_sequencia_p bigint) FROM PUBLIC;

