SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_prot_recebimento ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(01);
qt_total_w	bigint := 0;
qt_parcial_w	bigint := 0;
qt_retirado_w	bigint := 0;

c01 CURSOR FOR
SELECT	dt_recebimento,
	dt_retirada
from	protocolo_doc_item
where	nr_sequencia = nr_sequencia_p;

c01_w	c01%rowtype;


BEGIN

if (nr_sequencia_p > 0) then
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		qt_total_w	:= qt_total_w + 1;

		if (c01_w.dt_recebimento IS NOT NULL AND c01_w.dt_recebimento::text <> '') and (coalesce(c01_w.dt_retirada::text, '') = '') then
			qt_parcial_w := qt_parcial_w + 1;
		end if;

		if (c01_w.dt_retirada IS NOT NULL AND c01_w.dt_retirada::text <> '') then
			qt_retirado_w := qt_retirado_w + 1;
		end if;
		end;
	end loop;
	close C01;

	if (qt_total_w <> 0 and qt_parcial_w <> 0) and
		((qt_parcial_w = qt_total_w) or
		((qt_parcial_w + qt_retirado_w) = qt_total_w)) then
		ds_retorno_w := 'R';
	elsif (qt_parcial_w < qt_total_w and qt_parcial_w <> 0 ) then
		ds_retorno_w := 'P';
	elsif (qt_total_w = 0 and qt_parcial_w = 0 ) then
		ds_retorno_w := 'V';
	else 	ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
