SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_status_questionario ( nr_seq_que_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

/* retorno da funcion:
P = Pendente (Não iniciado)
A = Em andamento (Há pergunta respondida, porém não todas)
F = Finalizado (Respondidas todas as perguntas)
*/
ds_retorno_w			varchar(1);
qt_perguntas_w			bigint;
qt_perguntas_respondidas_w	bigint;


BEGIN
select	count(1)
into STRICT	qt_perguntas_w
from	que_atendimento_questao a,
	que_questao b
where	a.nr_seq_questao = b.nr_sequencia
and	a.nr_seq_que_atendimento = nr_seq_que_atendimento_p
and	b.ie_local_resposta = 'P';

select	count(1)
into STRICT	qt_perguntas_respondidas_w
from	que_atendimento_questao a,
	que_questao b
where	a.nr_seq_questao = b.nr_sequencia
and	a.nr_seq_que_atendimento = nr_seq_que_atendimento_p
and	b.ie_local_resposta = 'P'
and exists (	SELECT	1
		from	que_atendimento_questao_op x
		where	x.nr_seq_que_atend_questao = a.nr_sequencia);

if (qt_perguntas_respondidas_w = qt_perguntas_w) then
	ds_retorno_w	:= 'F';
elsif (qt_perguntas_respondidas_w = 0) then
	ds_retorno_w	:= 'P';
else
	ds_retorno_w	:= 'A';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
